<?php    $host = 'localhost';    $login = 'root';    $password = '';    $dbName = "db_kit";    try    {        $dbh = new PDO('mysql:host='. $host .';dbname='.$dbName, $login, $password, array(            PDO::ATTR_PERSISTENT => true        ));        $dbh -> exec("SET CHARACTER SET utf8");    }    catch (PDOException $e)    {        print "Error!: " . $e->getMessage() . "<br/>";        die();    }